const calculateLCS = (text1, text2, i, j, obj) => {
  if (i === 0 || j === 0) {
    return 0;
  }

  if (obj[i + "-" + j] !== undefined) {
    return obj[i + "-" + j];
  }

  if (text1[i-1] === text2[j-1]) {
    return obj[i, j] = 1 + calculateLCS(text1, text2, i - 1, j - 1, obj);
  }

  const first = calculateLCS(text1, text2, i - 1, j, obj);  
  const second = calculateLCS(text1, text2, i, j - 1, obj);

  
  return obj[i, j] = Math.max(first, second);
};

var longestCommonSubsequence = function (text1, text2) {
  const obj = {};
  return calculateLCS(text1, text2, text1.length, text2.length, obj);
};

